import React, { useState } from 'react';
import { Connection, Keypair, PublicKey } from '@solana/web3.js';
import { Token } from '@solana/spl-token';

const TransferToken = () => {
  const [mintAddress, setMintAddress] = useState('');
  const [destination, setDestination] = useState('');
  const [amount, setAmount] = useState(0);
  const [message, setMessage] = useState('');

  const transferToken = async () => {
    const connection = new Connection(process.env.REACT_APP_SOLANA_NETWORK);
    const payer = Keypair.generate();
    const mint = new PublicKey(mintAddress);
    const token = new Token(connection, mint, TOKEN_PROGRAM_ID, payer);
    const sourceTokenAccount = await token.getOrCreateAssociatedAccountInfo(payer.publicKey);
    const destinationTokenAccount = await token.getOrCreateAssociatedAccountInfo(new PublicKey(destination));
    await token.transfer(sourceTokenAccount.address, destinationTokenAccount.address, payer, [], amount);
    setMessage(`Transferred ${amount} tokens to ${destinationTokenAccount.address.toBase58()}`);
  };

  return (
    <div>
      <h2>Transfer Token</h2>
      <input 
        type="text" 
        placeholder="Mint Address" 
        value={mintAddress}
        onChange={(e) => setMintAddress(e.target.value)} 
      />
      <input 
        type="text" 
        placeholder="Destination Address" 
        value={destination}
        onChange={(e) => setDestination(e.target.value)} 
      />
      <input 
        type="number" 
        placeholder="Amount" 
        value={amount}
        onChange={(e) => setAmount(e.target.value)} 
      />
      <button onClick={transferToken}>Transfer</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default TransferToken;
