// src/POS.js
import React, { useState } from 'react';
import { Connection, clusterApiUrl, PublicKey } from '@solana/web3.js';
import { createQR, encodeURL, findReference, validateTransfer } from '@solana/pay';
import BigNumber from 'bignumber.js';
import {
    Container,
    Typography,
    Box,
    Button,
    List,
    ListItem,
    ListItemText,
    Divider,
    Snackbar,
    Alert,
    Paper,
    Grid
} from '@mui/material';
import { AddShoppingCart, Payment } from '@mui/icons-material';

const POS = () => {
    const [cart, setCart] = useState([]);
    const [total, setTotal] = useState(0);
    const [transactionConfirmed, setTransactionConfirmed] = useState(false);
    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [paymentError, setPaymentError] = useState('');

    const merchantPublicKey = new PublicKey('3z1YByoDdZ1sAeT1bGQpH3cGpLzQFk1NRUQ5xZueeF6T');

    const addItemToCart = (item) => {
        setCart([...cart, item]);
        setTotal(total + item.price);
    };

    const handlePayment = async () => {
        try {
            const connection = new Connection(clusterApiUrl('devnet'));
            const amount = new BigNumber(total);
            const reference = new PublicKey('3J98t1WpEZ73CNmQviecrnyiWrnqRhWNLy');
            const paymentURL = encodeURL({
                recipient: merchantPublicKey,
                amount,
                reference,
                label: 'Your Store',
                message: 'Thank you for your purchase!',
            });

            const qr = createQR(paymentURL, 512, 'transparent');
            document.getElementById('qr-container').innerHTML = '';
            document.getElementById('qr-container').appendChild(qr);

            const signatureInfo = await new Promise((resolve, reject) => {
                const interval = setInterval(async () => {
                    try {
                        const signature = await findReference(connection, reference, { finality: 'confirmed' });
                        if (signature) {
                            clearInterval(interval);
                            resolve(signature);
                        }
                    } catch (e) {
                        // Keep checking
                    }
                }, 1000);
            });

            const isValid = await validateTransfer(connection, signatureInfo.signature, {
                recipient: merchantPublicKey,
                amount,
                reference,
            });

            if (isValid) {
                setTransactionConfirmed(true);
                setSnackbarOpen(true);
            } else {
                throw new Error('Transaction validation failed');
            }
        } catch (error) {
            console.error('Payment failed:', error);
            setPaymentError('Payment failed. Please try again.');
            setSnackbarOpen(true);
        }
    };

    return (
        <Container maxWidth="sm" sx={{ mt: 4 }}>
            <Paper elevation={3} sx={{ p: 3 }}>
                <Typography variant="h4" component="h1" gutterBottom align="center">
                    POS System
                </Typography>

                <Box sx={{ mb: 2 }}>
                    <Typography variant="h6">Cart</Typography>
                    <List>
                        {cart.map((item, index) => (
                            <ListItem key={index}>
                                <ListItemText primary={item.name} secondary={`$${item.price.toFixed(2)}`} />
                            </ListItem>
                        ))}
                    </List>
                    <Divider />
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
                        <Typography variant="h6">Total</Typography>
                        <Typography variant="h6">${total.toFixed(2)}</Typography>
                    </Box>
                </Box>

                <Box id="qr-container" sx={{ textAlign: 'center', mb: 2 }}></Box>

                <Grid container spacing={2}>
                    <Grid item xs={6}>
                        <Button
                            variant="contained"
                            startIcon={<AddShoppingCart />}
                            fullWidth
                            onClick={() => addItemToCart({ name: 'Item 1', price: 10.00 })}
                        >
                            Add Item 1 ($10)
                        </Button>
                    </Grid>
                    <Grid item xs={6}>
                        <Button
                            variant="contained"
                            color="primary"
                            startIcon={<Payment />}
                            fullWidth
                            onClick={handlePayment}
                            disabled={total === 0}
                        >
                            Pay Now
                        </Button>
                    </Grid>
                </Grid>

                <Snackbar
                    open={snackbarOpen}
                    autoHideDuration={6000}
                    onClose={() => setSnackbarOpen(false)}
                >
                    {transactionConfirmed ? (
                        <Alert onClose={() => setSnackbarOpen(false)} severity="success" sx={{ width: '100%' }}>
                            Payment Confirmed! Thank you for your purchase.
                        </Alert>
                    ) : (
                        <Alert onClose={() => setSnackbarOpen(false)} severity="error" sx={{ width: '100%' }}>
                            {paymentError}
                        </Alert>
                    )}
                </Snackbar>
            </Paper>
        </Container>
    );
};

export default POS;
